import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './RecipeGenerator.css'; // Optional: For styling

const RecipeGenerator = () => {
    const [ingredients, setIngredients] = useState('');
    const [recipe, setRecipe] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false); // Add loading state

    const handleInputChange = (e) => setIngredients(e.target.value);

    const handleSearch = async () => {
        setLoading(true);
        const ingredientArray = ingredients
            .toLowerCase()
            .split(',')
            .map((ingredient) => ingredient.trim());

        try {
            const response = await fetch('http://localhost:3000/recipes');
            if (response.ok) {
                const data = await response.json();
                const matchedRecipe = findBestRecipe(data, ingredientArray);

                if (matchedRecipe) {
                    setRecipe(matchedRecipe);
                    setError(null);
                } else {
                    setError('No recipe found with the provided ingredients');
                    setRecipe(null);
                }
            } else {
                setError('Failed to fetch recipes');
            }
        } catch (error) {
            setError('An error occurred while fetching recipes');
        } finally {
            setLoading(false); // Ensure loading ends
        }
    };

    const findBestRecipe = (recipes, ingredientArray) => {
        let bestMatch = null;
        let maxMatchCount = 0;

        recipes.forEach((recipe) => {
            const recipeIngredients = recipe.ingredients
                .toLowerCase()
                .split(',')
                .map((ingredient) => ingredient.trim());

            const matchCount = recipeIngredients.filter((ingredient) =>
                ingredientArray.includes(ingredient)
            ).length;

            if (matchCount > maxMatchCount) {
                maxMatchCount = matchCount;
                bestMatch = recipe;
            }
        });

        return bestMatch;
    };

    const highlightIngredients = (recipeIngredients, inputIngredients) => {
        return recipeIngredients
            .split(',')
            .map((ing) => {
                const trimmedIng = ing.trim();
                const isHighlighted = inputIngredients.includes(trimmedIng.toLowerCase());
                return isHighlighted ? <mark key={trimmedIng}>{trimmedIng}</mark> : trimmedIng;
            })
            .reduce((prev, curr) => [prev, ', ', curr]); // Join ingredients with commas
    };

    return (
        <div className="recipe-generator">
            <h1>Recipe Generator</h1>
            <p>Tell us your ingredients, and weâ€™ll give you the best possible recipe!</p>

            <input
                type="text"
                value={ingredients}
                onChange={handleInputChange}
                placeholder="Enter ingredients, separated by commas"
            />
            <button className="btn" onClick={handleSearch} disabled={loading}>
                {loading ? 'Generating...' : 'Generate Recipe'}
            </button>

            {error && <p className="error-message">{error}</p>}

            {recipe && (
                <Link to={`/recipes/${recipe._id}`} className="recipe-card">
                    <h2>{recipe.name}</h2>
                    <img
                        src={`http://localhost:3000/${recipe.mainImage}`}
                        alt={recipe.name}
                        className="recipe-image"
                    />
                    <p>{recipe.description}</p>

                    <h3>Ingredients:</h3>
                    <ul>
                        {highlightIngredients(recipe.ingredients, ingredients.split(',').map((ing) => ing.trim().toLowerCase()))}
                    </ul>

                    <h3>Steps:</h3>
                    <ol>
                        {recipe.steps.map((step, index) => (
                            <li key={index}>{step}</li>
                        ))}
                    </ol>
                </Link>
            )}
        </div>
    );
};

export default RecipeGenerator;
