import React, { useState } from 'react';

const CreateRecipe = () => {
    const [name, setName] = useState('');
    const [ingredients, setIngredients] = useState('');
    const [steps, setSteps] = useState(['']);
    const [cuisine, setCuisine] = useState('');
    const [dietaryRestrictions, setDietaryRestrictions] = useState('');
    const [mealType, setMealType] = useState('');

    const handleAddStep = () => {
        setSteps([...steps, '']);
    };

    const handleRemoveStep = (index) => {
        const newSteps = steps.filter((_, i) => i !== index);
        setSteps(newSteps);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Submit logic here
    };

    return (
        <div className="create-recipe">
            <h2>Create Your Recipe</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Recipe Name" value={name} onChange={(e) => setName(e.target.value)} required />
                <textarea placeholder="Ingredients" value={ingredients} onChange={(e) => setIngredients(e.target.value)} required />

                <select value={mealType} onChange={(e) => setMealType(e.target.value)} required>
                    <option value="">Select Type of Meal</option>
                    <option value="Breakfast">Breakfast</option>
                    <option value="Lunch">Lunch</option>
                    <option value="Dinner">Dinner</option>
                </select>

                <input type="text" placeholder="Cuisine" value={cuisine} onChange={(e) => setCuisine(e.target.value)} required />
                <input type="text" placeholder="Dietary Restrictions" value={dietaryRestrictions} onChange={(e) => setDietaryRestrictions(e.target.value)} />

                <div className="steps-container">
                    {steps.map((step, index) => (
                        <div key={index} className="step-input">
                            <input
                                type="text"
                                value={step}
                                onChange={(e) => {
                                    const newSteps = [...steps];
                                    newSteps[index] = e.target.value;
                                    setSteps(newSteps);
                                }}
                                required
                            />
                            <button type="button" onClick={() => handleRemoveStep(index)}>Remove</button>
                        </div>
                    ))}
                    <button type="button" onClick={handleAddStep}>Add Step</button>
                </div>

                <button type="submit">Add Recipe</button>
            </form>
        </div>
    );
};

export default CreateRecipe;
