import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';

const SearchComponent = ({ onResults, onNewSearch, isLoading }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [activeTab, setActiveTab] = useState('name');
    const [difficulty, setDifficulty] = useState('');

    const handleInputChange = (e) => setSearchTerm(e.target.value);
    const handleDifficultyChange = (e) => setDifficulty(e.target.value);
    const handleTabChange = (tab) => setActiveTab(tab);

    const handleSearchSubmit = async () => {
        const queryParams = {
            [activeTab]: searchTerm,
            ...(difficulty && { difficulty }),
        };

        const formattedSearch = Object.entries(queryParams)
            .filter(([_, value]) => value)
            .map(([key, value]) => `${key}: ${value}`)
            .join(', ');

        await searchRecipes(queryParams);

        if (formattedSearch) onNewSearch(formattedSearch);
    };

    const searchRecipes = async (queryParams) => {
        const query = new URLSearchParams(queryParams).toString();
        try {
            const response = await fetch(`http://localhost:3000/recipes?${query}`);
            if (response.ok) {
                const data = await response.json();
                onResults(data);
            } else {
                console.error('Failed to fetch recipes:', response.statusText);
                onResults([]);
            }
        } catch (error) {
            console.error('Error fetching recipes:', error);
            onResults([]);
        }
    };

    return (
        <div className="search-box">
            <div className="search-input">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleInputChange}
                    placeholder={`Search by ${activeTab}`}
                />
                <button className="btn" onClick={handleSearchSubmit} disabled={isLoading}>
                    {isLoading ? 'Searching...' : <FontAwesomeIcon icon={faSearch} />}
                </button>
            </div>

            <div className="tab-options">
                {['name', 'ingredient'].map((tab) => (
                    <button
                        key={tab}
                        className={`tab ${activeTab === tab ? 'active' : ''}`}
                        onClick={() => handleTabChange(tab)}
                    >
                        Search by {tab.charAt(0).toUpperCase() + tab.slice(1)}
                    </button>
                ))}
            </div>

            <div className="options">
                <select value={difficulty} onChange={handleDifficultyChange}>
                    <option value="">Sort by difficulty</option>
                    <option value="Easy">Easy</option>
                    <option value="Medium">Medium</option>
                    <option value="Hard">Hard</option>
                </select>
            </div>
        </div>
    );
};

export default SearchComponent;
